import React, {useState, useEffect} from "react";
import {Card,Button} from 'antd';
import axios from 'axios';
const {Meta} = Card;

function Sport() {

    const [news, setNews] = useState ([]);

    useEffect(() => {
        const loadNews = async () => {
        const response = await axios.get("https://newsdata.io/api/1/news?apikey=pub_89691201dfe74703a37f4055688639e89a76&country=tr,ae,gb,us&language=en&category=sports  ");

        setNews(response.data.results);
    };

    loadNews();
    }, []);

    console.log('news', news);
  return (
    <div className="">
      {news && news.map((item, index) => {
        return(
            <Card
            key={index}
            hoverable style={{width: "70%"}}
            cover={<img alt="image" src= {item.image_url}/>}>

                <Meta title={item.title} description={item.description}/>
                <a href={item.link} target='blank' rel="noopener noreferrer">
                    <Button type="primary" style={{marginTop: '10px'}}>Read More</Button>
                </a>
            </Card>
        )
      })}
    </div>
  )
}
export default Sport;
