import '../style/#'
import React, { useState, useEffect } from "react";
import { Card, Button } from 'antd';
import axios from 'axios';
const { Meta } = Card;


function Weather() {

    const [weather, setweather] = useState([]);

    useEffect(() => {
        const loadweather = async () => {
            const response = await axios.get("https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/jordan?unitGroup=uk&key=BU2Q48AQMNDLLBQPYB472B393&contentType=json ");

            setweather(response.data.days);
        };
        loadweather();
    }, []);

    console.log('weather', weather);
    return (

        <div className="container-fluid" style={{ width: '60%' }}>
            <div class="line top">
                <div>Weather News</div>
            </div>
            {weather && weather.map((item, index) => {
                return (
                    <div className="content_wrapper" id="app">
  <div v-if="infos.city">
    <h1>
      Weather
      <div className="date">today</div>
    </h1>
    <div className="weather_card">
      <div className="weather_card_img">
        <div className="weather_card_state">
          <span className="material-icons">weatherIcon</span>
        </div>
      </div>
      <div className="weather_card_infos">
        <div className="weather_card_infos_title">infos.city infos.country</div>
        <div className="weather_card_infos_temp">celsius°C / fahrenheit°F</div>
        <div className="bottom">
          <hr />
          <div className="weather_card_infos_bottom">
            humidity: infos.humidity - pressure: infos.pressure Pa
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

