import React, { useState, useEffect } from "react";
import { Card, Button } from 'antd';
import axios from 'axios';
const { Meta } = Card;


function Weather() {

    const [weather, setweather] = useState([]);

    useEffect(() => {
        const loadweather = async () => {
            const response = await axios.get("https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/jordan?unitGroup=uk&key=BU2Q48AQMNDLLBQPYB472B393&contentType=json ");

            setweather(response.data.days);
        };
        loadweather();
    }, []);

    console.log('weather', weather);
    return (

        <div className="container-fluid" style={{ width: '60%' }}>
            <div class="line top">
                <div>Weather News</div>
            </div>
            {weather && weather.map((item, index) => {
                return (
                    <div key={index}>
                        <div class="row" >
                            <article class="col-md-12 article-list">
                                    <div class="inner">
                                        {/* <figure>
                                            <a href="single.html">
                                                <img src={item.urlToImage} alt="Sample Article" />
                                            </a>
                                        </figure> */}
                                        <div class="details">
                                            <div class="detail">
                                                <div class="category">
                                                    <a href="#">Jordan News</a>
                                                </div>
                                                <div class="time">{item.datetime}</div>
                                                {/* <div class="time">December 19, 2016</div> */}
                                            </div>
                                            <h1><a href="single.html">{item.temp} </a></h1>
                                            <p>
                                                {item.description}
                                            </p>
                                            <p>
                                                {item.sunrise}
                                            </p>
                                            <p>
                                                {item.sunset}
                                            </p>
                                            <footer>
                                                {/* <a href="#" class="love"><i class="ion-android-favorite-outline"></i> <div>273</div></a> */}
                                                {/* <a class="btn btn-primary more" href={item.condition}>
                                                </a> */}
                                            </footer>
                                        </div>
                                    </div>
                                
                            </article>

                        </div>
                        <br />

                    </div>
                )

            })}
        </div>
    )
}
export default Weather;
